        public static string JSBeautify(string input )
        {
            return (new JSBeautifyLib.JSBeautify(input, jsbo)).GetResult();
        }
        public static string RandName(int min = 2, int max = 4)
        {

            List<string[]> multi = new List<string[]>() { partsA, partsB, partsC };

            List<string> name = new List<string>();
            int parts = _rnd.Next(min, max);
            for (int i = 0; i < parts; i++)
            {
                var partype = multi[_rnd.Next(0, multi.Count)];
                name.Add(partype[_rnd.Next(0, partype.Length)]);
                if (_rnd.Next(10) == 8)
                {
                    name.Add((_rnd.Next(2,8)).ToString());
                }
            }
            if (_rnd.Next(10) < 3)
            {
                name.Add("["+(_rnd.Next(5) * _rnd.Next(5) * _rnd.Next(5)).ToString() + "]");

            }
            //TOdo: add place to area name and append 'mix/jam/vip/cut'  field mix.. - may just append on save as? suffix method  

            return String.Join("_", name).ToLower().Replace("_[", "["); ;
        }
